hints.text:
if (jenisKendaraan === "motor") {
  let tarif = 2000;
  if (jam > 2) tarif += (jam - 2) * 1000;
  return tarif > 10000 ? 10000 : tarif;
} else {
  let tarif = 5000;
  if (jam > 2) tarif += (jam - 2) * 2000;
  return tarif > 25000 ? 25000 : tarif;
}
